import React, { useEffect, useState, useRef } from 'react';
import { Link } from 'react-router-dom';
import { motion, useScroll, useTransform, useInView } from 'framer-motion';
import { FaPhone, FaCalendarAlt, FaUserMd, FaHeart, FaShieldAlt, FaStar, FaArrowRight, FaCheckCircle, FaQuoteLeft, FaAward, FaArrowDown, FaCertificate, FaGraduationCap, FaMapMarkerAlt, FaCreditCard, FaEnvelope, FaHome, FaBuilding, FaLeaf, FaMountain, FaRoad, FaClock } from 'react-icons/fa';

const Home = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const { scrollY } = useScroll();
  const y1 = useTransform(scrollY, [0, 300], [0, -50]);
  const y2 = useTransform(scrollY, [0, 300], [0, -30]);

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };
    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  const services = [
    {
      icon: <FaUserMd className="w-7 h-7" />,
      title: "Diabetic Foot Care",
      description: "Specialized care for diabetic patients with comprehensive foot assessments, wound care, and evidence-based treatment protocols.",
      features: ["Comprehensive Risk Assessment", "Diabetic Wound Care", "Foot Health Education", "Circulation Monitoring"]
    },
    {
      icon: <FaHeart className="w-7 h-7" />,
      title: "Advanced Nail Treatment",
      description: "Professional nail care including ONYFIX pain-free correction treatment for ingrown nails and ToeFx technology for fungal infections.",
      features: ["ONYFIX Ingrown Treatment", "ToeFx Fungal Therapy", "Professional Nail Trimming", "Nail Health Assessment"]
    },
    {
      icon: <FaShieldAlt className="w-7 h-7" />,
      title: "Skin & Wound Care",
      description: "Expert treatment for corns, calluses, wounds, and skin conditions using advanced podiatry equipment and sterile techniques.",
      features: ["Corn & Callus Removal", "Basic Wound Care", "Skin Health Treatment", "Podiatry Drill Debridement"]
    },
    {
      icon: <FaStar className="w-7 h-7" />,
      title: "Specialized Senior Care",
      description: "Compassionate foot care for seniors, including those with dementia, mobility challenges, and complex medical conditions.",
      features: ["Dementia-Friendly Care", "Mobility Support", "Gentle Treatment", "Family Communication"]
    },
    {
      icon: <FaCheckCircle className="w-7 h-7" />,
      title: "Comprehensive Assessment",
      description: "Full foot health evaluation including circulation, sensation, and risk factor analysis with personalized care plans.",
      features: ["30-40 Min Assessment", "Risk Factor Analysis", "Care Plan Development", "Follow-up Scheduling"]
    },
    {
      icon: <FaAward className="w-7 h-7" />,
      title: "Preventive Care Programs",
      description: "Educational programs and preventive treatments to maintain optimal foot health and prevent complications.",
      features: ["Health Education", "Prevention Strategies", "Regular Maintenance", "Lifestyle Guidance"]
    }
  ];

  const testimonials = [
    {
      name: "Margaret Thompson",
      role: "Senior Client - Age 82",
      text: "The mobile service is a blessing! I can't drive anymore, and having professional foot care come to my home makes me feel safe and comfortable. The specialist is so patient and gentle with my diabetic feet.",
      rating: 5,
      image: "ðŸ‘µ",
      condition: "Diabetic Foot Care"
    },
    {
      name: "Robert Chen",
      role: "Family Caregiver",
      text: "My father has dementia, and finding someone who understands his needs was challenging. The foot care specialist is incredibly patient and kind, making each visit stress-free for both of us.",
      rating: 5,
      image: "ðŸ‘¨â€ðŸ‘©â€ðŸ‘§â€ðŸ‘¦",
      condition: "Dementia Care"
    },
    {
      name: "Eleanor Williams",
      role: "Retirement Home Resident",
      text: "I've been getting foot care for 3 years now. The specialist brings all sterile equipment and treats me with such dignity. My feet have never felt better, and my mobility has improved significantly.",
      rating: 5,
      image: "ðŸ‘©â€ðŸ¦³",
      condition: "Senior Mobility"
    },
    {
      name: "David Martinez",
      role: "Diabetic Patient - Type 2",
      text: "As a diabetic, I was worried about foot complications. The comprehensive care and education I received has been life-changing. Regular treatments have prevented serious issues.",
      rating: 5,
      image: "ðŸ‘¨â€âš•ï¸",
      condition: "Diabetic Prevention"
    },
    {
      name: "Joyce Patterson",
      role: "Senior Client - Age 78",
      text: "The ONYFIX treatment for my ingrown toenail was amazing! No surgery needed, and the pain is completely gone. I can walk comfortably again after months of discomfort.",
      rating: 5,
      image: "ðŸ‘©â€ðŸ¦±",
      condition: "ONYFIX Treatment"
    },
    {
      name: "Frank & Helen Morrison",
      role: "Elderly Couple",
      text: "We both receive care together in our home. It's wonderful to have someone who understands our needs as seniors. The 40-minute comprehensive assessments catch issues early.",
      rating: 5,
      image: "ðŸ‘«",
      condition: "Couple Care"
    }
  ];

  const stats = [
    { number: "500+", label: "Satisfied Patients", icon: <FaHeart className="w-5 h-5" /> },
    { number: "5+", label: "Years of Excellence", icon: <FaAward className="w-5 h-5" /> },
    { number: "15+", label: "Specialized Services", icon: <FaUserMd className="w-5 h-5" /> },
    { number: "100%", label: "Patient Satisfaction", icon: <FaStar className="w-5 h-5" /> }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.15,
        delayChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { y: 30, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        type: "spring",
        stiffness: 100,
        damping: 12
      }
    }
  };

  const AnimatedCounter = ({ end, duration = 2.5 }) => {
    const [count, setCount] = useState(0);
    const ref = useRef(); // Changed from React.useRef() to useRef()
    const isInView = useInView(ref, { once: true });

    useEffect(() => {
      if (isInView) {
        let startTime;
        const animate = (currentTime) => {
          if (!startTime) startTime = currentTime;
          const progress = Math.min((currentTime - startTime) / (duration * 1000), 1);
          setCount(Math.floor(progress * parseInt(end)));
          if (progress < 1) requestAnimationFrame(animate);
        };
        requestAnimationFrame(animate);
      }
    }, [isInView, end, duration]);

    return <span ref={ref}>{count}{end.includes('+') ? '+' : end.includes('%') ? '%' : ''}</span>;
  };

  return (
    <div className="overflow-hidden">
      {/* Subtle Mouse Follower */}
      <motion.div
        className="fixed w-3 h-3 bg-primary/30 rounded-full pointer-events-none z-50 mix-blend-multiply"
        animate={{
          x: mousePosition.x - 6,
          y: mousePosition.y - 6
        }}
        transition={{ type: "spring", stiffness: 500, damping: 28 }}
      />

      {/* Hero Section */}
      <section className="relative bg-gradient-to-br from-primary via-secondary to-primary text-white py-24 min-h-screen flex items-center overflow-hidden">
        {/* Professional Background Pattern */}
        <div className="absolute inset-0 opacity-5">
          <motion.div
            className="absolute inset-0"
            style={{
              backgroundImage: `radial-gradient(circle at 20% 20%, white 1px, transparent 1px)`,
              backgroundSize: '40px 40px'
            }}
            animate={{
              backgroundPosition: ['0px 0px', '40px 40px']
            }}
            transition={{
              duration: 30,
              repeat: Infinity,
              ease: "linear"
            }}
          />
        </div>

        <motion.div style={{ y: y1 }} className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative z-10">
          <div className="text-center">
            <motion.div
              initial={{ scale: 0, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              transition={{ duration: 0.6, type: "spring", stiffness: 100 }}
              className="mb-8"
            >
              <span className="inline-flex items-center px-6 py-3 bg-white/15 backdrop-blur-md rounded-full text-sm font-medium border border-white/20">
                <FaAward className="w-4 h-4 mr-2" />
                Professional Foot Care Excellence
              </span>
            </motion.div>
            
            <motion.h1
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
              className="text-5xl md:text-7xl font-bold mb-8 leading-tight tracking-tight"
            >
              <span className="block mb-2">Healthy Feet,</span>
              <span className="block bg-gradient-to-r from-white via-yellow-200 to-white bg-clip-text text-transparent">
                Happy Life
              </span>
            </motion.h1>
            
            <motion.p
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.4 }}
              className="text-xl md:text-2xl mb-12 max-w-4xl mx-auto leading-relaxed font-light"
            >
              Professional Foot Care & Wellness Services in Calgary, Alberta
              <span className="block mt-2 text-lg opacity-90">Licensed Practical Nurse â€¢ Advanced Foot Care Specialist</span>
            </motion.p>
            
            <motion.div
              variants={containerVariants}
              initial="hidden"
              animate="visible"
              className="flex flex-col sm:flex-row gap-6 justify-center mb-16"
            >
              <motion.div variants={itemVariants}>
                <Link
                  to="/book-appointment"
                  className="group bg-white text-primary px-10 py-4 rounded-full font-semibold hover:bg-yellow-50 transition-all duration-300 inline-flex items-center justify-center space-x-3 shadow-lg hover:shadow-xl transform hover:-translate-y-1"
                >
                  <FaCalendarAlt className="w-5 h-5" />
                  <span>Book Appointment</span>
                  <motion.div
                    className="w-0 group-hover:w-5 transition-all duration-300 overflow-hidden"
                  >
                    <FaArrowRight className="w-4 h-4" />
                  </motion.div>
                </Link>
              </motion.div>
              
              <motion.div variants={itemVariants}>
                <motion.a
                  href="tel:403-903-4293"
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  className="border-2 border-white/80 text-white px-10 py-4 rounded-full font-semibold hover:bg-white hover:text-primary transition-all duration-300 inline-flex items-center justify-center space-x-3 backdrop-blur-sm"
                >
                  <FaPhone className="w-5 h-5" />
                  <span>403-903-4293</span>
                </motion.a>
              </motion.div>
            </motion.div>

            {/* Professional Scroll Indicator */}
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 1.2 }}
              className="absolute bottom-8 left-1/2 transform -translate-x-1/2"
            >
              <motion.div
                animate={{ y: [0, 8, 0] }}
                transition={{ duration: 2, repeat: Infinity }}
                className="text-white/60 text-center"
              >
                <div className="w-6 h-10 border-2 border-white/40 rounded-full mx-auto mb-2 flex justify-center">
                  <motion.div
                    animate={{ y: [0, 12, 0] }}
                    transition={{ duration: 2, repeat: Infinity }}
                    className="w-1 h-3 bg-white/60 rounded-full mt-2"
                  />
                </div>
                <p className="text-xs font-medium">Scroll to explore</p>
              </motion.div>
            </motion.div>
          </div>
        </motion.div>
      </section>

      {/* Professional Stats Section */}
      <motion.section 
        style={{ y: y2 }}
        className="py-20 bg-white relative border-b border-gray-100"
      >
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
            className="grid grid-cols-2 md:grid-cols-4 gap-8"
          >
            {stats.map((stat, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, scale: 0.8 }}
                whileInView={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                viewport={{ once: true }}
                className="text-center p-6 rounded-2xl bg-gradient-to-br from-gray-50 to-white border border-gray-100 hover:shadow-lg transition-all duration-300"
              >
                <div className="flex justify-center mb-3 text-primary">
                  {stat.icon}
                </div>
                <motion.div
                  whileHover={{ scale: 1.05 }}
                  className="text-3xl md:text-4xl font-bold text-primary mb-2"
                >
                  <AnimatedCounter end={stat.number} />
                </motion.div>
                <p className="text-gray-600 font-medium text-sm">{stat.label}</p>
              </motion.div>
            ))}
          </motion.div>
        </div>
      </motion.section>

      {/* Mobile Service Section - NEW */}
      <section className="py-24 bg-gradient-to-br from-blue-50 to-indigo-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <motion.div
            initial={{ opacity: 0, y: 30 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
            className="text-center mb-16"
          >
            <motion.span
              initial={{ opacity: 0, scale: 0 }}
              whileInView={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5 }}
              viewport={{ once: true }}
              className="inline-block px-4 py-2 bg-primary/10 text-primary rounded-full text-sm font-semibold mb-6 tracking-wide"
            >
              MOBILE CARE SERVICE
            </motion.span>
            <h2 className="text-4xl md:text-5xl font-bold text-gray-900 mb-6 tracking-tight">
              Professional Care in Your Home
            </h2>
            <p className="text-xl text-gray-600 max-w-3xl mx-auto leading-relaxed">
              Experience the convenience and comfort of professional foot care in your own home. 
              We bring our expertise directly to you at no extra cost.
            </p>
          </motion.div>

          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
            {/* Mobile Service Benefits */}
            <motion.div
              initial={{ opacity: 0, x: -50 }}
              whileInView={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.8 }}
              viewport={{ once: true }}
              className="space-y-8"
            >
              <div className="flex items-start space-x-4">
                <div className="w-12 h-12 bg-primary rounded-full flex items-center justify-center flex-shrink-0">
                  <FaHeart className="w-6 h-6 text-white" />
                </div>
                <div>
                  <h3 className="text-xl font-bold text-gray-900 mb-2">Comfort & Convenience</h3>
                  <p className="text-gray-600">Receive professional care in the familiar comfort of your own home, eliminating travel stress and waiting rooms.</p>
                </div>
              </div>

              <div className="flex items-start space-x-4">
                <div className="w-12 h-12 bg-secondary rounded-full flex items-center justify-center flex-shrink-0">
                  <FaShieldAlt className="w-6 h-6 text-white" />
                </div>
                <div>
                  <h3 className="text-xl font-bold text-gray-900 mb-2">Sterile Equipment</h3>
                  <p className="text-gray-600">We bring high-quality, sterile single-use equipment that is responsibly disposed of to prevent infection transmission.</p>
                </div>
              </div>

              <div className="flex items-start space-x-4">
                <div className="w-12 h-12 bg-primary rounded-full flex items-center justify-center flex-shrink-0">
                  <FaUserMd className="w-6 h-6 text-white" />
                </div>
                <div>
                  <h3 className="text-xl font-bold text-gray-900 mb-2">Specialized Care</h3>
                  <p className="text-gray-600">Expert care for seniors, diabetic patients, and those with dementia or mobility challenges in a familiar environment.</p>
                </div>
              </div>

              <div className="flex items-start space-x-4">
                <div className="w-12 h-12 bg-secondary rounded-full flex items-center justify-center flex-shrink-0">
                  <FaCheckCircle className="w-6 h-6 text-white" />
                </div>
                <div>
                  <h3 className="text-xl font-bold text-gray-900 mb-2">No Extra Cost</h3>
                  <p className="text-gray-600">Mobile service comes at no additional charge. We serve Calgary and surrounding areas including personal homes and care facilities.</p>
                </div>
              </div>
            </motion.div>

            {/* Service Areas */}
            <motion.div
