import React, { useEffect, useState, useRef } from 'react';
import { Link } from 'react-router-dom';
import { motion, useScroll, useTransform, useInView } from 'framer-motion';
import { FaPhone, FaCalendarAlt, FaUserMd, FaHeart, FaShieldAlt, FaStar, FaArrowRight, FaCheckCircle, FaQuoteLeft, FaAward } from 'react-icons/fa';

const Home = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const { scrollY } = useScroll();
  const y1 = useTransform(scrollY, [0, 300], [0, -50]);
  const y2 = useTransform(scrollY, [0, 300], [0, -30]);

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };
    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  const services = [
    {
      icon: <FaUserMd className="w-7 h-7" />,
      title: "Diabetic Foot Care",
      description: "Specialized care for diabetic patients with comprehensive foot assessments and evidence-based treatment protocols.",
      features: ["Risk Assessment", "Wound Care", "Education"]
    },
    {
      icon: <FaHeart className="w-7 h-7" />,
      title: "Nail Care & Treatment",
      description: "Professional nail cutting, trimming, and treatment for ingrown nails, fungal infections, and other conditions.",
      features: ["Nail Trimming", "Fungal Treatment", "Ingrown Care"]
    },
    {
      icon: <FaShieldAlt className="w-7 h-7" />,
      title: "Skin Treatment",
      description: "Expert care for corns, calluses, dry skin, and other dermatological foot conditions using advanced techniques.",
      features: ["Corn Removal", "Callus Care", "Skin Health"]
    },
    {
      icon: <FaStar className="w-7 h-7" />,
      title: "Wellness Services",
      description: "Comprehensive foot wellness including therapeutic massage, education, and preventive care programs.",
      features: ["Foot Massage", "Prevention", "Education"]
    }
  ];

  const testimonials = [
    {
      name: "Sarah Mitchell",
      role: "Diabetic Patient",
      text: "Sima provided exceptional care for my diabetic foot condition. Her professional approach and genuine care made all the difference in my treatment journey.",
      rating: 5,
      image: "ğŸ‘©â€âš•ï¸"
    },
    {
      name: "John Davidson",
      role: "Senior Client",
      text: "Finally found a foot care specialist who truly understands patient needs. The clinic environment is pristine and the service is outstanding.",
      rating: 5,
      image: "ğŸ‘¨â€ğŸ’¼"
    },
    {
      name: "Maria Lopez",
      role: "Regular Client",
      text: "Outstanding professional service! My feet feel remarkably better after treatment. I highly recommend this clinic to anyone seeking quality foot care.",
      rating: 5,
      image: "ğŸ‘©â€ğŸ“"
    }
  ];

  const stats = [
    { number: "500+", label: "Satisfied Patients", icon: <FaHeart className="w-5 h-5" /> },
    { number: "5+", label: "Years of Excellence", icon: <FaAward className="w-5 h-5" /> },
    { number: "15+", label: "Specialized Services", icon: <FaUserMd className="w-5 h-5" /> },
    { number: "100%", label: "Patient Satisfaction", icon: <FaStar className="w-5 h-5" /> }
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.15,
        delayChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { y: 30, opacity: 0 },
    visible: {
      y: 0,
      opacity: 1,
      transition: {
        type: "spring",
        stiffness: 100,
        damping: 12
      }
    }
  };

  const AnimatedCounter = ({ end, duration = 2.5 }) => {
    const [count, setCount] = useState(0);
    const ref = useRef(); // Changed from React.useRef() to useRef()
    const isInView = useInView(ref, { once: true });

    useEffect(() => {
      if (isInView) {
        let startTime;
        const animate = (currentTime) => {
          if (!startTime) startTime = currentTime;
          const progress = Math.min((currentTime - startTime) / (duration * 1000), 1);
          setCount(Math.floor(progress * parseInt(end)));
          if (progress < 1) requestAnimationFrame(animate);
        };
        requestAnimationFrame(animate);
      }
    }, [isInView, end, duration]);

    return <span ref={ref}>{count}{end.includes('+') ? '+' : end.includes('%') ? '%' : ''}</span>;
  };

  return (
    <div className="overflow-hidden">
      {/* Subtle Mouse Follower */}
      <motion.div
        className="fixed w-3 h-3 bg-primary/30 rounded-full pointer-events-none z-50 mix-blend-multiply"
        animate={{
          x: mousePosition.x - 6,
          y: mousePosition.y - 6
        }}
        transition={{ type: "spring", stiffness: 500, damping: 28 }}
      />

      {/* Hero Section */}
      <section className="relative bg-gradient-to-br from-primary via-secondary to-primary text-white py-24 min-h-screen flex items-center overflow-hidden">
        {/* Professional Background Pattern */}
        <div className="absolute inset-0 opacity-5">
          <motion.div
            className="absolute inset-0"
            style={{
              backgroundImage: `radial-gradient(circle at 20% 20%, white 1px, transparent 1px)`,
              backgroundSize: '40px 40px'
            }}
            animate={{
              backgroundPosition: ['0px 0px', '40px 40px']
            }}
            transition={{
              duration: 30,
              repeat: Infinity,
              ease: "linear"
            }}
          />
        </div>

        <motion.div style={{ y: y1 }} className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 relative z-10">
          <div className="text-center">
            <motion.div
              initial={{ scale: 0, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              transition={{ duration: 0.6, type: "spring", stiffness: 100 }}
              className="mb-8"
            >
              <span className="inline-flex items-center px-6 py-3 bg-white/15 backdrop-blur-md rounded-full text-sm font-medium border border-white/20">
                <FaAward className="w-4 h-4 mr-2" />
                Professional Foot Care Excellence
              </span>
            </motion.div>
            
            <motion.h1
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
              className="text-5xl md:text-7xl font-bold mb-8 leading-tight tracking-tight"
            >
              <span className="block mb-2">Healthy Feet,</span>
              <span className="block bg-gradient-to-r from-white via-yellow-200 to-white bg-clip-text text-transparent">
                Happy Life
              </span>
            </motion.h1>
            
            <motion.p
              initial={{ opacity: 0, y: 30 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.4 }}
              className="text-xl md:text-2xl mb-12 max-w-4xl mx-auto leading-relaxed font-light"
            >
              Professional Foot Care & Wellness Services in Calgary, Alberta
              <span className="block mt-2 text-lg opacity-90">Licensed Practical Nurse â€¢ Advanced Foot Care Specialist</span>
            </motion.p>
            
            <motion.div
              variants={containerVariants}
              initial="hidden"
              animate="visible"
              className="flex flex-col sm:flex-row gap-6 justify-center mb-16"
            >
              <motion.div variants={itemVariants}>
                <Link
                  to="/book-appointment"
                  className="group bg-white text-primary px-10 py-4 rounded-full font-semibold hover:bg-yellow-50 transition-all duration-300 inline-flex items-center justify-center space-x-3 shadow-lg hover:shadow-xl transform hover:-translate-y-1"
                >
                  <FaCalendarAlt className="w-5 h-5" />
                  <span>Book Appointment</span>
                  <motion.div
                    className="w-0 group-hover:w-5 transition-all duration-300 overflow-hidden"
                  >
                    <FaArrowRight className="w-4 h-4" />
                  </motion.div>
                </Link>
              </motion.div>
              
              <motion.div variants={itemVariants}>
                <motion.a
                  href="tel:403-903-4293"
                  whileHover={{ scale: 1.02 }}
                  whileTap={{ scale: 0.98 }}
                  className="border-2 border-white/80 text-white px-10 py-4 rounded-full font-semibold hover:bg-white hover:text-primary transition-all duration-300 inline-flex items-center justify-center space-x-3 backdrop-blur-sm"
                >
                  <FaPhone className="w-5 h-5" />
                  <span>403-903-4293</span>
                </motion.a>
              </motion.div>
            </motion.div>

            {/* Professional Scroll Indicator */}
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ delay: 1.2 }}
              className="absolute bottom-8 left-1/2 transform -translate-x-1/2"
            >
              <motion.div
                animate={{ y: [0, 8, 0] }}
                transition={{ duration: 2, repeat: Infinity }}
                className="text-white/60 text-center"
