                <div className="w-6 h-10 border-2 border-white/40 rounded-full mx-auto mb-2 flex justify-center">
                  <motion.div
                    animate={{ y: [0, 12, 0] }}
                    transition={{ duration: 2, repeat: Infinity }}
                    className="w-1 h-3 bg-white/60 rounded-full mt-2"
                  />
