import { FaMapMarkerAlt, FaCreditCard, FaShieldAlt, FaCalendarAlt } from 'react-icons/fa';

export const faq = [
  {
    id: 'service-area',
    title: 'Service Area & Location',
    icon: FaMapMarkerAlt,
    items: [
      {
        q: 'Where do you provide services?',
        a: 'We provide mobile foot care services throughout Calgary and surrounding areas, including Airdrie, Cochrane, Okotoks, and Chestermere. We come directly to your home, care facility, or retirement home.'
      },
      {
        q: 'Do you have a clinic location?',
        a: 'We specialize in mobile services, bringing professional foot care directly to you. This ensures comfort and convenience, especially for seniors and those with mobility challenges.'
      },
      {
        q: 'How far do you travel?',
        a: 'We typically serve within a 50km radius of Calgary. For locations outside this area, please contact us to discuss availability and potential travel fees.'
      }
    ]
  },
  {
    id: 'payment',
    title: 'Payment & Insurance',
    icon: FaCreditCard,
    items: [
      {
        q: 'What payment methods do you accept?',
        a: 'We accept electronic transfer (eTransfer), cash, cheque, and offer insurance direct billing for eligible plans. Payment is due at the time of service.'
      },
      {
        q: 'Do you direct bill insurance?',
        a: 'Yes, we offer direct billing for many insurance plans. Please contact us with your insurance information to verify coverage and eligibility.'
      },
      {
        q: 'What are your service rates?',
        a: 'Our comprehensive 30-40 minute assessments are competitively priced. Contact us for current rates and package options for regular maintenance care.'
      }
    ]
  },
  {
    id: 'equipment',
    title: 'Equipment & Safety',
    icon: FaShieldAlt,
    items: [
      {
        q: 'How do you ensure sterile conditions?',
        a: 'We use only sterile, single-use instruments for each client. All equipment is professionally sterilized, and we follow strict infection prevention protocols to ensure your safety.'
      },
      {
        q: 'What equipment do you bring?',
        a: 'We bring all necessary professional equipment including podiatry drill, sterile instruments, ONYFIX application tools, and specialized wound care supplies. You do not need to provide anything.'
      },
      {
        q: 'Are your services safe for diabetics?',
        a: 'Absolutely. We specialize in diabetic foot care with advanced training in wound prevention and management. Our sterile protocols are especially important for diabetic patients.'
      }
    ]
  },
  {
    id: 'booking',
    title: 'Booking & Appointments',
    icon: FaCalendarAlt,
    items: [
      {
        q: 'How do I book an appointment?',
        a: 'You can book by calling (403) 903-4293, using our online booking form, or sending an email. We typically respond within 24 hours to schedule your appointment.'
      },
      {
        q: 'How long is each appointment?',
        a: 'Our comprehensive assessments take 30-40 minutes, allowing time for thorough examination, treatment, and patient education.'
      },
      {
        q: 'Do you offer regular maintenance schedules?',
        a: 'Yes, we recommend regular maintenance every 6-8 weeks for optimal foot health. We can set up recurring appointments to ensure consistent care and early problem detection.'
      },
      {
        q: 'What if I need to cancel or reschedule?',
        a: 'Please provide 24-hour notice for cancellations or rescheduling. We understand that health situations can change and will work with you to find suitable alternative times.'
      }
    ]
  }
];

export default faq;
